#获取数字转换成大写字母
sub getColumnName{  
    $first;  
    $lastvalue;  
    $columnNum=$_[0];
    #print "column的值为".$columnNum."\n";
    $result = "";  

    $first = int($columnNum / 27);  
    $lastvalue = $columnNum - ($first * 26);  
  	
  	#print "看一下".$first."            ".$lastvalue."\n"; 
  	$temp;
    if ($first gt 0){ #lt小于 gt大于 ne不等于 
    		$temp=$first+64;
    		#print $temp."\n";
        $result = chr($temp);  
      }
  
    if (lastvalue gt 0){
    		$temp=$lastvalue+64;
    		#print $temp."\n";
        $result = $result.chr($temp); 
      } 
 			#$result=$result;
      #print "暂时值为：".$result."\n"; 
  
} 
 
